
public class Saludo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int op1 = 5;
		int op2 = 6;
		
		op1++;
		System.out.println("Operando 1: " + op1);
		op1--;
		
		op1=34;
		
		System.out.println("Operando 1: " + op1);
		System.out.println("Operando 2: " + op2);
		
		System.out.println("Suma: " + (op1 + op2));

	}

}
